  # A relative (local) URL pointing to a raster image in the WEBP format.
  class WebpImageURL
    attr_accessor :value

    def self.from_json_data(data)
      out = WebpImageURL.new
      out.value = Datasworn.from_json_data(String, data)
      out
    end

    def to_json_data
      Datasworn.to_json_data(value)
    end
